import React from 'react';
import { Line } from 'react-chartjs-2';
import styles from './CoordinatorDashboard.module.css';

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// // Register Chart.js components
// ChartJS.register(
//   CategoryScale,
//   LinearScale,
//   PointElement,
//   LineElement,
//   Title,
//   Tooltip,
//   Legend
// );

const Graph = ({ scheduledTours = 0, completedTours = 0, graphData }) => {
  // Graph configuration
  const data = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'],
    datasets: [
      {
        label: 'Scheduled Tours',
        data: graphData?.scheduled || [],
        borderColor: '#ff8c00',
        backgroundColor: 'rgba(255, 140, 0, 0.2)',
        tension: 0.4,
      },
      {
        label: 'Completed Tours',
        data: graphData?.completed || [],
        borderColor: '#007bff',
        backgroundColor: 'rgba(0, 123, 255, 0.2)',
        tension: 0.4,
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
    },
    scales: {
      x: {
        grid: { display: false },
      },
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div className={styles.graphContainer}>
      <div className={styles.graphHeader}>
        <h2>Number of Tours</h2>
        <div className={styles.tourStats}>
          <p>
            <strong>Scheduled:</strong> {scheduledTours}
          </p>
          <p>
            <strong>Completed:</strong> {completedTours}
          </p>
        </div>
      </div>
      <div className={styles.graphBody}>
        <Line data={data} options={options} />
      </div>
    </div>
  );
};

export default Graph;
