import React, { useState } from 'react';
import styles from './CoordinatorDashboardHighSchool.module.css';
import { FaBell, FaCog, FaSearch } from 'react-icons/fa';

const Header = ({ title }) => {
  const [filter, setFilter] = useState('Date Updated');

  const handleSearch = () => {
    const searchInput = document.querySelector(`.${styles.searchBar} input`).value;
    console.log('Searching for:', searchInput); // Replace with actual search logic
  };

  const handleDropdownChange = () => {
    setFilter(filter === 'Date Updated' ? 'High School Name' : 'Date Updated');
  };

  return (
    <div className={styles.header}>
      {/* Title */}
      <h1 className={styles.headerTitle}>{title}</h1>

      {/* Row for Search and Buttons */}
      <div className={styles.buttonRow}>
        {/* Search Bar */}
        <div className={styles.searchBar}>
          <button className={styles.searchIconButton} onClick={handleSearch}>
            <FaSearch className={styles.searchIcon} />
          </button>
          <input type="text" placeholder="Search High School Name" />
        </div>

        {/* Dropdown Button */}
        <div className={styles.dropdown}>
          <button
            className={styles.dropdownButton}
            onClick={handleDropdownChange}
          >
            {filter} <span className={styles.dropdownArrow}>▼</span>
          </button>
        </div>

        {/* Remove Selected Button */}
        <button className={styles.removeButton}>Remove Selected</button>
      </div>

      {/* User Info */}
      <div className={styles.userSection}>
        {/* Notification and Settings */}
        <button className={styles.iconButton}>
          <FaBell className={styles.notificationIcon} />
          <span className={styles.notificationDot}></span>
        </button>
        <button className={styles.iconButton}>
          <FaCog />
        </button>

        {/* User Avatar */}
        <div className={styles.userAvatar}>
          <div className={styles.avatarCircle}></div>
          <div className={styles.userInfoText}>
            <p className={styles.userName}>Nabila A.</p>
            <p className={styles.userRole}>Coordinator</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Header;
