import React from 'react';
import styles from './CoordinatorDashboardToursAndFairs.module.css';
import { FaCalendarAlt, FaClock } from 'react-icons/fa';

const RightSidebar = ({ selectedDay, events }) => {
  const filteredEvents = events.filter((event) => event.date === selectedDay);

  return (
    <div className={styles.rightSidebar}>
      <h3 className={styles.sidebarTitle}>
        Schedule Details {selectedDay && `- October ${selectedDay}, 2024`}
      </h3>
      {filteredEvents.length > 0 ? (
        filteredEvents.map((event, index) => (
          <div key={index} className={styles.eventCard}>
            <div className={styles.eventType}>
              {event.type === 'tour' ? 'Tour' : 'Fair'}
            </div>
            <div className={styles.eventDetails}>
              <FaCalendarAlt className={styles.icon} />
              <span>{`October ${event.date}, 2024`}</span>
            </div>
            <div className={styles.eventDetails}>
              <FaClock className={styles.icon} />
              <span>{event.time || '09:00 - 11:00'}</span>
            </div>
          </div>
        ))
      ) : (
        <p className={styles.noEvents}>No events for this day</p>
      )}
      <button className={styles.viewAllButton}>View All</button>
    </div>
  );
};

export default RightSidebar;
