import React, { useState, useEffect } from 'react';
import Sidebar from './Sidebar';
import InfoCard from './InfoCard';
import Graph from './Graph';
import { FaUsers, FaChalkboardTeacher, FaRoute, FaBuilding } from 'react-icons/fa';
import styles from './CoordinatorDashboard.module.css';

const Dashboard = () => {
  const dummyInfoData = [
    {
      title: 'Advisors',
      value: 34,
      icon: <FaUsers style={{ color: '#ffffff' }} />, // Icon in white
      bgColor: '#e0a800', // Gold circle background
    },
    {
      title: 'Guides',
      value: 53,
      icon: <FaChalkboardTeacher style={{ color: '#ffffff' }} />, // Icon in white
      bgColor: '#20c997', // Green circle background
    },
    {
      title: 'Tours',
      value: 49,
      icon: <FaRoute style={{ color: '#ffffff' }} />, // Icon in white
      bgColor: '#007bff', // Blue circle background
    },
    {
      title: 'Fairs',
      value: 9,
      icon: <FaBuilding style={{ color: '#ffffff' }} />, // Icon in white
      bgColor: '#6610f2', // Purple circle background
    },
  ];

  // Dummy graph data
  const [graphData, setGraphData] = useState({
    scheduled: [5, 10, 15, 20, 25, 30, 35, 40, 45, 50, 55, 60],
    completed: [3, 8, 12, 18, 22, 28, 34, 38, 42, 47, 52, 57],
  });

  const [scheduledTours, setScheduledTours] = useState(49); // Example value
  const [completedTours, setCompletedTours] = useState(38); // Example value

  // Simulating backend fetch for the graph data
  useEffect(() => {
    // Replace with real API call in the future
    const fetchData = async () => {
      // Simulating a delay
      await new Promise((resolve) => setTimeout(resolve, 1000));
      setGraphData({
        scheduled: [5, 12, 18, 25, 30, 40, 45, 50, 55, 65, 75, 85],
        completed: [3, 10, 15, 20, 25, 30, 35, 40, 45, 50, 55, 60],
      });
      setScheduledTours(55);
      setCompletedTours(42);
    };
    fetchData();
  }, []);

  return (
    <div className={styles.dashboard}>
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content */}
      <div className={styles.mainContent}>
        <h1 className={styles.dashboardTitle}>Dashboard</h1>

        {/* Info Cards */}
        <div className={styles.infoCardsContainer}>
          {dummyInfoData.map((data, index) => (
            <InfoCard
              key={index}
              title={data.title}
              value={data.value}
              icon={data.icon}
              bgColor={data.bgColor}
            />
          ))}
        </div>

        {/* Graph */}
        <Graph
          graphData={graphData}
          scheduledTours={scheduledTours}
          completedTours={completedTours}
        />
      </div>
    </div>
  );
};

export default Dashboard;
