import React from 'react';import { PieChart, Pie, Cell, Legend, Tooltip } from 'recharts';
import styles from './CoordinatorDashboard.module.css'; // Assuming you add necessary CSS here

const CustomPieChart = () => {
  const [chartData, setChartData] = useState([]);

  // Dummy data to simulate initial state
  const dummyData = [
    { name: 'Engineering', value: 30, color: '#374151' }, // Navy
    { name: 'Business Administration', value: 15, color: '#f97316' }, // Orange
    { name: 'Art, Design, and Architecture', value: 20, color: '#d946ef' }, // Pink
    { name: 'Others', value: 35, color: '#2563eb' }, // Blue
  ];

  // Fetch data from the backend
  useEffect(() => {
    // Simulated API call
    const fetchChartData = async () => {
      // Simulate delay
      await new Promise((resolve) => setTimeout(resolve, 1000));

      // Replace with real API response
      const backendData = [
        { name: 'Engineering', value: 30, color: '#374151' },
        { name: 'Business Administration', value: 15, color: '#f97316' },
        { name: 'Art, Design, and Architecture', value: 20, color: '#d946ef' },
        { name: 'Others', value: 35, color: '#2563eb' },
      ];

      setChartData(backendData);
    };

    fetchChartData();
  }, []);

  // Render loading state if no data yet
  if (!chartData.length) {
    return <p>Loading Pie Chart...</p>;
  }

  return (
    <div className={styles.pieChartContainer}>
      <h2 className={styles.chartTitle}>Departments of Interest</h2>
      <PieChart width={400} height={300}>
        <Pie
          data={chartData}
          cx="50%" // Center X
          cy="50%" // Center Y
          outerRadius={100} // Radius size
          fill="#8884d8"
          dataKey="value" // Key used for pie chart
          nameKey="name" // Key used for names
          label={(entry) => `${entry.name} (${entry.value}%)`} // Add labels
        >
          {chartData.map((entry, index) => (
            <Cell key={`cell-${index}`} fill={entry.color} />
          ))}
        </Pie>
        <Tooltip />
        <Legend verticalAlign="bottom" height={36} />
      </PieChart>
    </div>
  );
};

export default CustomPieChart;
