import React from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Register components for Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);
import styles from './CoordinatorDashboard.module.css';

const Graph = ({ scheduledTours = 0, completedTours = 0, graphData }) => {
  // Graph configuration
  const data = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'], // Static months
    datasets: [
      {
        label: 'Scheduled Tours',
        data: graphData.scheduled, // Pass scheduled tours data
        borderColor: '#ff8c00', // Orange for Scheduled Tours
        backgroundColor: 'rgba(255, 140, 0, 0.2)', // Transparent orange fill
        tension: 0.4, // Smooth curve
      },
      {
        label: 'Completed Tours',
        data: graphData.completed, // Pass completed tours data
        borderColor: '#007bff', // Blue for Completed Tours
        backgroundColor: 'rgba(0, 123, 255, 0.2)', // Transparent blue fill
        tension: 0.4,
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false, // Allows the graph to grow/shrink with the container
    plugins: {
      legend: {
        position: 'top', // Legend position
      },
    },
    scales: {
      x: {
        grid: { display: false }, // No gridlines on X-axis
      },
      y: {
        beginAtZero: true,
        ticks: {
          stepSize: 5, // Customizable steps for Y-axis
        },
      },
    },
  };

  return (
    <div className={styles.graphContainer}>
      <div className={styles.graphHeader}>
        <h2>Number of Tours</h2>
        <div className={styles.tourStats}>
          <p>
            <strong>Scheduled: </strong>
            {scheduledTours}
          </p>
          <p>
            <strong>Completed: </strong>
            {completedTours}
          </p>
        </div>
      </div>
      <div className={styles.graphBody}>
        <Line data={data} options={options} />
      </div>
    </div>
  );
};

export default Graph;
