import React, { useState, useEffect } from 'react';
import Sidebar from './Sidebar';
import InfoCard from './InfoCard';
import Graph from './Graph';
import { FaUsers, FaChalkboardTeacher, FaRoute, FaBuilding } from 'react-icons/fa';
import styles from './CoordinatorDashboard.module.css';

const Dashboard = () => {
  const [loading, setLoading] = useState(true); // Loading state
  const [graphData, setGraphData] = useState({
    scheduled: [],
    completed: [],
  });
  const [scheduledTours, setScheduledTours] = useState(0);
  const [completedTours, setCompletedTours] = useState(0);

  const dummyInfoData = [
    {
      title: 'Advisors',
      value: 34,
      icon: <FaUsers style={{ color: '#ffffff' }} />,
      bgColor: '#e0a800', // Gold circle background
    },
    {
      title: 'Guides',
      value: 53,
      icon: <FaChalkboardTeacher style={{ color: '#ffffff' }} />,
      bgColor: '#20c997', // Green circle background
    },
    {
      title: 'Tours',
      value: 49,
      icon: <FaRoute style={{ color: '#ffffff' }} />,
      bgColor: '#007bff', // Blue circle background
    },
    {
      title: 'Fairs',
      value: 9,
      icon: <FaBuilding style={{ color: '#ffffff' }} />,
      bgColor: '#6610f2', // Purple circle background
    },
  ];

  useEffect(() => {
    let isMounted = true;

    const fetchData = async () => {
      await new Promise((resolve) => setTimeout(resolve, 1000)); // Simulated delay
      if (isMounted) {
        setGraphData({
          scheduled: [5, 12, 18, 25, 30, 40, 45, 50, 55, 65, 75, 85],
          completed: [3, 10, 15, 20, 25, 30, 35, 40, 45, 50, 55, 60],
        });
        setScheduledTours(55);
        setCompletedTours(42);
        setLoading(false);
      }
    };

    fetchData();

    return () => {
      isMounted = false;
    };
  }, []);

  return (
    <div className={styles.dashboard}>
      <Sidebar />
      <div className={styles.mainContent}>
        <h1 className={styles.dashboardTitle}>Dashboard</h1>
        <div className={styles.infoCardsContainer}>
          {dummyInfoData.map((data, index) => (
            <InfoCard
              key={index}
              title={data.title}
              value={data.value}
              icon={data.icon}
              bgColor={data.bgColor}
            />
          ))}
        </div>
        {loading ? (
          <p>Loading graph...</p>
        ) : (
          <Graph
            graphData={graphData}
            scheduledTours={scheduledTours}
            completedTours={completedTours}
          />
        )}
      </div>
    </div>
  );
};

export default Dashboard;
