import React from 'react';
import { Line } from 'react-chartjs-2';
import styles from './CoordinatorDashboard.module.css';
import 'chart.js/auto'; // Automatically registers all required components

const Graph = ({ scheduledTours = 0, completedTours = 0, graphData = { scheduled: [], completed: [] } }) => {
  const chartData = {
    labels: [
      'Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun',
      'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec',
    ],
    datasets: [
      {
        label: 'Scheduled Tours',
        data: graphData.scheduled,
        borderColor: '#FFA500',
        backgroundColor: 'rgba(255, 165, 0, 0.2)',
        tension: 0.4,
      },
      {
        label: 'Completed Tours',
        data: graphData.completed,
        borderColor: '#007BFF',
        backgroundColor: 'rgba(0, 123, 255, 0.2)',
        tension: 0.4,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
    },
    scales: {
      x: {
        grid: {
          display: false,
        },
      },
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <div className={styles.graphContainer}>
      <div className={styles.graphHeader}>
        <h2>Number of Tours</h2>
        <div className={styles.tourStats}>
          <p>
            <strong>Scheduled:</strong> {scheduledTours}
          </p>
          <p>
            <strong>Completed:</strong> {completedTours}
          </p>
        </div>
      </div>
      <div className={styles.graphBody}>
        <Line data={chartData} options={chartOptions} />
      </div>
    </div>
  );
};

export default Graph;
