import React, { useState } from 'react';
import styles from './CoordinatorDashboardTours&Fairs.module.css';

const Calendar = ({ onDayClick }) => {
  const [currentMonth, setCurrentMonth] = useState('October');
  const [currentYear, setCurrentYear] = useState(2024);

  // Dummy data for events
  const events = [
    { date: 10, type: 'tour', count: 2 },
    { date: 15, type: 'fair', count: 1 },
    { date: 20, type: 'fair', count: 3 },
    { date: 18, type: 'tour', count: 1 },
    { date: 29, type: 'tour', count: 1 },
  ];

  const daysInMonth = 31; // Static for simplicity

  // Render days with event indicators
  const renderDays = () => {
    const days = [];
    for (let i = 1; i <= daysInMonth; i++) {
      const event = events.find((e) => e.date === i);
      days.push(
        <div
          key={i}
          className={`${styles.calendarDay} ${
            event
              ? event.type === 'tour'
                ? styles.tourDay
                : styles.fairDay
              : ''
          }`}
          onClick={() => onDayClick(i)}
        >
          <span>{i}</span>
          {event && <div className={styles.eventIndicator}>{event.count}+</div>}
        </div>
      );
    }
    return days;
  };

  // Month toggle handler
  const changeMonth = (direction) => {
    if (direction === 'prev') {
      setCurrentMonth('September');
      setCurrentYear(2024);
    } else {
      setCurrentMonth('November');
      setCurrentYear(2024);
    }
  };

  return (
    <div className={styles.calendarContainer}>
      <div className={styles.calendarHeader}>
        <div className={styles.monthControls}>
          <button
            className={styles.toggleMonthButton}
            onClick={() => changeMonth('prev')}
          >
            &lt;
          </button>
          <span>{`${currentMonth} ${currentYear}`}</span>
          <button
            className={styles.toggleMonthButton}
            onClick={() => changeMonth('next')}
          >
            &gt;
          </button>
        </div>
      </div>
      <div className={styles.calendarGrid}>
        <div className={styles.dayLabel}>Sun</div>
        <div className={styles.dayLabel}>Mon</div>
        <div className={styles.dayLabel}>Tue</div>
        <div className={styles.dayLabel}>Wed</div>
        <div className={styles.dayLabel}>Thu</div>
        <div className={styles.dayLabel}>Fri</div>
        <div className={styles.dayLabel}>Sat</div>
        {renderDays()}
      </div>
    </div>
  );
};

export default Calendar;
